@RestController
@RequestMapping("/user")
public class UserController {

    // Obsługa żądania GET, zwraca informacje o użytkowniku
    @GetMapping("/{id}")
    public User getUserById(@PathVariable Long id) {
        // Symulacja zwrotu obiektu użytkownika
        return new User(id, "Zhang San", 30);
    }

    // Klasa encji użytkownika
    static class User {
        private Long id;
        private String name;
        private int age;

        public User(Long id, String name, int age) {
            this.id = id;
            this.name = name;
            this.age = age;
        }

        // Pomijamy getter i setter
    }
}
