@RestController
@RequestMapping("/user")
public class UserController {

    // Processa GET, retorna informações do usuário
    @GetMapping("/{id}")
    public User getUserById(@PathVariable Long id) {
        // Simula retorno de objeto usuário
        return new User(id, "张三", 30);
    }

    // Classe entidade de usuário
    static class User {
        private Long id;
        private String name;
        private int age;

        public User(Long id, String name, int age) {
            this.id = id;
            this.name = name;
            this.age = age;
        }

        // getters e setters omitidos
    }
}
