@RestController
@RequestMapping("/user")
public class UserController {

    // Обработка GET-запроса, возвращающего информацию о пользователе
    @GetMapping("/{id}")
    public User getUserById(@PathVariable Long id) {
        // Имитация возврата объекта пользователя
        return new User(id, "张三", 30);
    }

    // Класс пользователя
    static class User {
        private Long id;
        private String name;
        private int age;

        public User(Long id, String name, int age) {
            this.id = id;
            this.name = name;
            this.age = age;
        }

        // геттеры и сеттеры опущены
    }
}
```

