@RestController
@RequestMapping("/user")
public class UserController {

    // Xử lý GET, trả về thông tin người dùng
    @GetMapping("/{id}")
    public User getUserById(@PathVariable Long id) {
        // Giả lập trả về đối tượng người dùng
        return new User(id, "Trần Văn A", 30);
    }

    // Lớp thực thể người dùng
    static class User {
        private Long id;
        private String name;
        private int age;

        public User(Long id, String name, int age) {
            this.id = id;
            this.name = name;
            this.age = age;
        }

        // Bỏ qua getter và setter
    }
}
```

