}
},
"foutafhandeling": {
"type": "array",
"items": {
"type": "string",
"description": "Instructies voor het omgaan met veelvoorkomende problemen of randgevallen"
}
},
"ethische\_overwegingen": {
"type": "array",
"items": {
"type": "string",
"description": "Relevante ethische of nalevingsvereisten"
}
},
