            }
            .poem-line:first-child {
                margin-top: -20px;
            }
            .poem-line:last-child {
                margin-top: 80px; /* Увеличаване на интервала между редовете на поезията */
            }
            .title {
                position: absolute;
                left: 20px;
                bottom: 40px; /* Увеличаване на долното отстояние */
                writing-mode: vertical-rl;
                text-orientation: upright;
                font-size: 32px;
