/**
 * عمليات الإعداد
 */
export interface SettingsAction {
  /**
   * إعادة تعيين الإعدادات
   */
  resetSettings: () => void;
  /**
   * تعيين الإعدادات العامة
   * @param settings - الإعدادات العامة
   */
  setGlobalSettings: (settings: GlobalSettingsState) => void;
  /**
   * تعيين إعدادات جزئية
   * @param settings - إعدادات جزئية
   */
  setSettings: (settings: Partial<ConfigSettings>) => void;
  /**
   * تعيين وضع السمة
   * @param themeMode - وضع السمة
   */
  setThemeMode: (themeMode: ThemeMode) => void;
  /**
   * تبديل تبويب الشريط الجانبي
   * @param key - التبويب المختار في الشريط الجانبي
   */
  switchSideBar: (key: SidebarTabKey) => void;
}
```
