/**
 * Операции за настройки
 */
export interface SettingsAction {
  /**
   * Нулиране на настройките
   */
  resetSettings: () => void;
  /**
   * Задаване на глобални настройки
   * @param settings - Глобални настройки
   */
  setGlobalSettings: (settings: GlobalSettingsState) => void;
  /**
   * Задаване на частични настройки
   * @param settings - Частични настройки
   */
  setSettings: (settings: Partial<ConfigSettings>) => void;
  /**
   * Задаване на режим на тема
   * @param themeMode - Режим на тема
   */
  setThemeMode: (themeMode: ThemeMode) => void;
  /**
   * Превключване на страничната лента
   * @param key - Избран ключ на страничната лента
   */
  switchSideBar: (key: SidebarTabKey) => void;
}
```
