 */
export interface SettingsAction {
  /**
   * Einstellungen zurücksetzen
   */
  resetSettings: () => void;
  /**
   * Globale Einstellungen setzen
   * @param settings - Globale Einstellungen
   */
  setGlobalSettings: (settings: GlobalSettingsState) => void;
  /**
   * Teilweise Einstellungen setzen
   * @param settings - Teilweise Konfigurationseinstellungen
   */
  setSettings: (settings: Partial<ConfigSettings>) => void;
  /**
   * Themenmodus setzen
   * @param themeMode - Themenmodus
   */
  setThemeMode: (themeMode: ThemeMode) => void;
  /**
   * Seitenleiste umschalten
   * @param key - Ausgewählter Seitenleisten-Tab
   */
  switchSideBar: (key: SidebarTabKey) => void;
}
