```ts
/**
 * عملیات تنظیمات
 */
export interface SettingsAction {
  /**
   * بازنشانی تنظیمات
   */
  resetSettings: () => void;
  /**
   * تنظیم تنظیمات سراسری
   * @param settings - تنظیمات سراسری
   */
  setGlobalSettings: (settings: GlobalSettingsState) => void;
  /**
   * تنظیم بخشی از تنظیمات
   * @param settings - بخشی از تنظیمات
   */
  setSettings: (settings: Partial<ConfigSettings>) => void;
  /**
   * تنظیم حالت تم
   * @param themeMode - حالت تم
   */
  setThemeMode: (themeMode: ThemeMode) => void;
  /**
   * تغییر تب نوار کناری
   * @param key - تب انتخاب شده نوار کناری
   */
  switchSideBar: (key: SidebarTabKey) => void;
}
```
