/**
 * Azioni di configurazione
 */
export interface SettingsAction {
  /**
   * Reimposta le impostazioni
   */
  resetSettings: () => void;
  /**
   * Imposta le impostazioni globali
   * @param settings - Impostazioni globali
   */
  setGlobalSettings: (settings: GlobalSettingsState) => void;
  /**
   * Imposta le impostazioni parziali
   * @param settings - Impostazioni parziali
   */
  setSettings: (settings: Partial<ConfigSettings>) => void;
  /**
   * Imposta la modalità tema
   * @param themeMode - Modalità tema
   */
  setThemeMode: (themeMode: ThemeMode) => void;
  /**
   * Cambia la scheda della barra laterale
   * @param key - Scheda selezionata della barra laterale
   */
  switchSideBar: (key: SidebarTabKey) => void;
}
```
